/* coche une valeur par défaut a l'interieur de l'accordion selectionné (voir l'export d'un exercice WIMS)*/
$('#js-accordion_export-WIMS').on('toggled', function (event, accordion) {
    target_id = $(this).find('.content.active').attr("id");
    if (target_id != undefined)
    {
        $("#export_format").prop('value', target_id);
        target_default = $('#' + target_id + '_default');
        target_default.prop('checked', true);
    }
});

/* Ferme le reveal une fois qu'on estime le téléchargement terminé (après 2s)*/
$('.close-download-modal').on('click', function(event) {
    setTimeout(function() {//calls event after a certain time
       $('#reveal-main').foundation('reveal','close');
    }, 1000);

});

